
using System.Diagnostics;

const string line = "";
var puzzleOne = (bool useExample) =>
{
    var map = new HashSet<char>();
    for (var i = 0; i < line.Length; i++ )
    {
        for (var x = i; x < i + 4; x++)
        {
            if (map.Add(line[x]))
            {
                if(x == i + 3)
                {
                    return x + 1;
                }
            } else
            {
                break;
            }
        }
        map.Clear();
    }    
    return 0;
};

var puzzleTwo = (bool useExample) =>
{
    var map = new HashSet<char>();
    for (var i = 0; i < line.Length; i++)
    {
        for (var x = i; x < i + 14; x++)
        {
            if (map.Add(line[x]))
            {
                if (x == i + 13)
                {
                    return x + 1;
                }
            }
            else
            {
                break;
            }
        }
        map.Clear();
    }
    return 0;
};

var puzzleOne2 = (bool useExample) =>
{
    foreach (string line in System.IO.File.ReadLines(useExample ? @"InputExample.txt" : @"Input.txt"))
    {
        for (var i = 0; i < line.Length; i++)
        {
            var target = line.Substring(i, 4);
            if(target.ToCharArray().Distinct().Count() == 4)
            {
                Console.WriteLine(target);
                Console.WriteLine(i + 4);
                break;
            }
        }
    }
    return 0;
};


var puzzleTwo2 = (bool useExample) =>
{
    foreach (string line in System.IO.File.ReadLines(useExample ? @"InputExample.txt" : @"Input.txt"))
    {
        for (var i = 0; i < line.Length; i++)
        {
            var target = line.Substring(i, 14);
            if (target.ToCharArray().Distinct().Count() == 14)
            {
                Console.WriteLine(i + 14);
                break;
            }
        }

    }

    return 0;
};

var watch = Stopwatch.StartNew();
Console.WriteLine("Puzzle one: " + puzzleOne(false));
watch.Stop();

Console.WriteLine(
    $"The Execution time of the program is {watch.ElapsedMilliseconds}ms");
